mapper.select:
 	select *
	from(
	    select rownum as rnum,SearchSharingResult.*
	    from ( 
	    	select * 
			from sharing
			where sno is not null

			order by sno asc
	    )  SearchSharingResult

	    where rownum <= #{endRow}		 		    		    
	)		 
	where rnum >= #{startRow}

mapper.update:
update sharing
set title = #{title}, content = #{content}, updatedate = sysdate
where sno = #{sno}

update sharing
set viewcnt = viewcnt+1
where sno = #{sno}
